mapper.select:
SELECT QUANTITY
FROM LINEITEM
WHERE ORDERID = #{orderId} AND ITEMID = #{itemId}

mapper.update:
UPDATE ORDERSTATUS SET
    TIMESTAMP = #{orderDate,jdbcType=TIMESTAMP},
    STATUS = #{status,jdbcType=VARCHAR}
WHERE ORDERID = #{orderId,jdbcType=NUMERIC}

UPDATE ORDERSTATUS SET
    STATUS = #{status}
WHERE ORDERID = #{orderId}
