mapper.update:
UPDATE INVENTORY SET
  QTY = QTY - #{increment}
WHERE ITEMID = #{itemId}

mapper.select:
SELECT QTY AS value
FROM INVENTORY
WHERE ITEMID = #{itemId}

mapper.update:
UPDATE INVENTORY SET
    ITEMID = #{item.itemId},
    QTY = #{item.quantity}
WHERE ITEMID = #{oldId}

UPDATE ITEM
SET STATUS = #{status}
WHERE ITEMID = #{itemId}
